function addItem(){$("#add-item-error").hide();var n=$("#add-item-title").val();$.post("/Todo/AddItem",{title:n},function(){window.location="/Todo"})}function markCompleted(n){n.disabled=!0;$.post("/Todo/MarkDone",{id:n.name},function(){var t=n.parentElement.parentElement;$(t).addClass("done")})}$(document).ready(function(){$("#add-item-button").on("click",addItem);$(".done-checkbox").on("click",function(n){markCompleted(n.target)})});